import { InMemoryCache, ApolloClient } from "@apollo/client";
import { DefaultTheme } from "@react-navigation/native";
import { gql } from "@apollo/client";
const cache = new InMemoryCache({
  typePolicies: {
    Query: {
      fields: {
        nfts: {
          merge: (existing, incoming) => {
            return incoming;
          },
        },
      },
    },
  },
});
const client = new ApolloClient({
  uri: "http://192.168.10.8:3000/graphql",
  cache,
});
const theme = {
  ...DefaultTheme, // inherit DefaultTheme
  colors: {
    ...DefaultTheme.colors, // inherit DefaultTheme.colors
    background: "transparent", // override DefaultTheme.colors.background
  },
};
const fonts = {
  InterBold: require("../assets/fonts/Inter-Bold.ttf"),
  InterSemiBold: require("../assets/fonts/Inter-SemiBold.ttf"),
  InterMedium: require("../assets/fonts/Inter-Medium.ttf"),
  InterRegular: require("../assets/fonts/Inter-Regular.ttf"),
  InterLight: require("../assets/fonts/Inter-Light.ttf"),
};

const GET_DATA = gql`
  query getDATA {
    nfts {
      id
      name
      time
    }
  }
`;

export { fonts, client, theme, GET_DATA };
