import { createSlice } from "@reduxjs/toolkit";
import {
  assets,
  COLORS,
  SHADOWS,
  SIZES,
  FONTS,
  NFTData,
} from "../../constants";
const initialState = {
  assets, // might be changed in the future
  COLORS,
  SHADOWS,
  SIZES,
  FONTS,
  NFTData: [],
  status: "",
  readMore: false,
  text: "",
};

export const stylesReducer = createSlice({
  name: "stylesReducer",
  initialState,
  reducers: {
    setStatus: (state, { payload }) => {
      state.status = payload;
    },
    setNFT: (state, { payload }) => {
      state.NFTData = payload;
    },
    searchNFT: (state, { payload }) => {
      if (payload.length === 0) {
        state.NFTData = state.NFTData;
      }
      const filteredData = state.NFTData.filter((item) =>
        item.name.toLowerCase().includes(payload.toLowerCase())
      );
      if (filteredData.length === 0) {
        state.NFTData = state.NFTData;
      } else {
        state.NFTData = filteredData;
      }
    },
    setReadMore: (state, { payload }) => {
      state.readMore = payload;
    },
    setText: (state, { payload }) => {
      state.text = payload;
    },
  },
});

// Action creators are generated for each case reducer function
export const { searchNFT, setReadMore, setText, setNFT, setStatus } =
  stylesReducer.actions;
export const selectData = (state) => state.styleSlice;
export default stylesReducer.reducer;
